mapper.select:
SELECT a.id, a.store_name, a.store_column, a.store_img_path, d.id column_parent_id, b.column_name store_column_name, a.store_location, a.latitude, a.longitude, a.store_detail, a.store_discount, a.top_flag, a.view_count, a.state, a.create_time, a.store_price, a.store_stars, a.create_user_id, c.user_name create_user_name, b.type, a.store_phone
FROM sys_store a left join sys_column b on a.store_column = b.id left join sys_column d on d.id = b.parent_id left join sys_user c on c.id = a.create_user_id WHERE a.state = 'A'
and b.id = #{storeColumn}
and (d.id = #{columnParentId} or b.column_id = #{columnParentId})
and a.top_flag = #{topFlag}
and a.create_user_id = #{createUserId}
and a.store_name like concat(#{storeName},'%')
order by a.create_time desc,top_flag desc

mapper.update:
		update sys_store 
		set 
		top_flag = #{topFlag}

 		where id = #{id}

		update sys_store 
		set 
		store_stars = #{storeStars}
 		where id = #{id}
